'QUERIES'


SELECT ST_Distance(a.location, b.location)
FROM POST_HOLES a, C_DEBRIS b
WHERE a.post_holes_id=1 AND b.C_DEBRIS_ID=1;

'shortest distance between post holes group and lakes' - yes
select distinct on (p.group_by_closest) p.group_by_closest as post_holes_group_by_closest,
st_distance(p.location, l.location) as distance, l.LAKES_ID as lakes_lakes_id
from public.post_holes p, public.lakes l
order by p.group_by_closest, st_distance(p.location, l.location)
;

'closest point lake to 'group' of post holes' - done
select distinct on (p.group_by_closest) p.group_by_closest as post_holes_group_by_closest,
st_distance(p.location, l.location) as distance, l.LAKES_ID as lakes_lakes_id
from public.post_holes p, public.lakes l
order by p.group_by_closest, ST_ClosestPoint(p.location, l.location)
;

'what features are within the early holocene border (topo 2)?'
(select lakes.*
from lakes
left join landscape_relative_topo on lakes.topo = landscape_relative_topo.topo
group by lakes.lakes_id)

(select esturine.*
from esturine
left join landscape_relative_topo on esturine.topo = landscape_relative_topo.topo
group by esturine.esturine_id)

(select fluvially_relate.*
from fluvially_relate
left join landscape_relative_topo on fluvially_relate.topo = landscape_relative_topo.topo
group by fluvially_relate.fluvial_id)

(select sand_banks_marine.*
from sand_banks_marine
left join landscape_relative_topo on sand_banks_marine.topo = landscape_relative_topo.topo
group by sand_banks_marine.sand_banks_id)

(select post_holes.*
from post_holes
left join landscape_relative_topo on post_holes.topo = landscape_relative_topo.topo
group by post_holes.post_holes_id)

(select c_debris.*
from c_debris
left join landscape_relative_topo on c_debris.topo = landscape_relative_topo.topo
group by c_debris.c_debris_id)


